function MoveBackground() {
  function e() {
    TweenLite.to(".moving  .pic-product", 1, {
      x: 0,
      y: 0,
      z: 0,
      ease: Power0.easeOut
    }), TweenLite.to(".moving  .bg-clip", 1, {
      x: 0,
      y: 0,
      ease: Power1.easeOut
    }), TweenLite.to(".moving  .box-img", 1, {
      x: 0,
      y: 0,
      z: 0,
      ease: Power2.easeOut
    }), TweenLite.to(".moving  .item-faci-home:nth-child(1)", 1, {
      x: 0,
      y: 0,
      z: 0,
      ease: Power0.easeOut
    }), TweenLite.to(".moving  .item-faci-home:nth-child(2)", 1, {
      x: 0,
      y: 0,
      z: 0,
      ease: Power0.easeOut
    }), TweenLite.to(".moving  .item-faci-home:nth-child(3)", 1, {
      x: 0,
      y: 0,
      z: 0,
      ease: Power0.easeOut
    })
  }

  function t() {
    DX = a.X - i, DY = a.Y - l, MoveX = DY / l, MoveY = -(DX / i), Radius = Math.sqrt(Math.pow(MoveX, 2) + Math.pow(MoveY, 2)), Degree = 2 * Radius, TweenLite.to(".moving  .pic-product", 1, {
      x: 50 * MoveX,
      y: 50 * MoveY,
      z: 100 * Degree,
      ease: Power0.easeOut
    }), TweenLite.to(".moving  .bg-clip", 3, {
      x: 80 * MoveX,
      y: 80 * MoveY,
      ease: Power1.easeOut
    }), TweenLite.to(".moving  .box-img", 1, {
      x: 30 * MoveX,
      y: 30 * MoveY,
      z: 0,
      ease: Power2.easeOut
    }), TweenLite.to(".moving  .item-faci-home:nth-child(1)", 2, {
      x: 80 * MoveX,
      y: 80 * MoveY,
      z: 30 * Degree,
      ease: Power0.easeOut
    }), TweenLite.to(".moving  .item-faci-home:nth-child(2)", 1, {
      x: 50 * MoveX,
      y: 50 * MoveY,
      z: 160 * Degree,
      ease: Power1.easeOut
    }), TweenLite.to(".moving  .item-faci-home:nth-child(3)", 2, {
      x: 100 * MoveX,
      y: 100 * MoveY,
      z: 120 * Degree,
      ease: Power2.easeOut
    })
  }
  var o = null,
    a = {
      X: 0,
      Y: 0
    },
    i = $(window).width() / 2,
    l = $(window).height() / 2;
  $(".show-text .product-home, .show-text .box-facilities").addClass("moving"), $(".nav-click, .degree, .hotline, .subscribe, .box-nav").on("mouseenter", function() {
    cancelAnimationFrame(o), e()
  }), $(window).width() > 1100 ? $(".box-slider").on("mousemove", function(e) {
    a.X = e.pageX, a.Y = e.pageY, cancelAnimationFrame(o), o = requestAnimationFrame(t)
  }) : $(".box-slider").on("mousemove", function() {
    cancelAnimationFrame(o), e()
  }), $(window).resize(function() {
    $(window).width() > 1100 ? (i = $(window).width() / 2, l = $(window).height() / 2) : e()
  })
