function popupLoad(e) {
  $.ajax({
    url: e,
    cache: !1,
    success: function(e) {
      $(".details-content").remove(), $("body").append(e), $("form").length && FocusText(), $(window).width() <= 840 && ($(".details-text img").addClass("zoom-pic"), ZoomPic()), $(".details-content").stop().animate({
