          $(".loadx").remove()
        })
      }), $(".close-popup, .details-content span").on("click", function() {
        return $(".details-content").animate({
          opacity: 0
        }, 500, "linear", function() {
          $(".details-content").remove(), $(".overlay-dark").removeClass("show"), $("html, body").removeClass("no-scroll")
        }), !1
      })
    }
  })
}

function ZoomMap() {
  $(".viewer").addClass("desktop").addClass("fadein");
  var e = $(".viewer");
  e.find(".panzoom").panzoom({
    $zoomIn: $(".pic-zoom-in"),
    $zoomOut: $(".pic-zoom-out"),
    maxScale: 3,
    minScale: 1,
    increment: .3,
    contain: "automatic"
  }).panzoom("zoom");
  var t = e.find(".panzoom").panzoom();
  t.on("mousewheel.focal", function(e) {
    e.preventDefault();
    var o = e.delta || e.originalEvent.wheelDelta,
      a = o ? 0 > o : e.originalEvent.deltaY > 0;
    t.panzoom("zoom", a, {
      increment: .1,
      animate: !1,
      focal: e
    })
  }), $(".map-img").addClass("show"), ScaleMap(), setTimeout(function() {
    $(".apartment-pointer").addClass("show")
  }, 1e3)
}

function ScaleMap() {
  $(".panzoom").css({
    "-webkit-transform": "matrix(1, 0, 0, 1, 0, 0)",
    transform: "matrix(1, 0, 0, 1, 0, 0)"
  });
  var e = $(window).height() / 1100;
  $(".map-img.show").scale(e)
}

function FocusText() {
  $("input, textarea").focus(function() {
    $(this).parent().find(".holder").addClass("hide")
  }).focusout(function() {
    "" == $(this).val() && $(this).parent().find(".holder").removeClass("hide")
  })
}

