function detectBut() {
  $("#news-page").length && $(".link-page").hasClass("current") && $(window).width() <= 1100 && $(".news-list").each(function(e, t) {
    var o = $(t),
      a = $(t).find(".grid").offset().left,
      i = $(t).find(".link-page.current").offset().left,
      l = $(".news-list").width() / 2 - $(".link-page").width() / 2;
    $(o).stop().animate({
      scrollLeft: i - l - a
    }, "slow")
  })
}

function detectMargin() {
  var e = $(".full img").width(),
    t = $(".full  img").height(),
    o = $(window).height(),
    a = $(window).width();
  a > e ? $(".full img").css({
    "margin-left": a / 2 - e / 2
  }) : $(".full img").css({
    "margin-left": 0
  }), o > t ? $(".full img").css({
    "margin-top": o / 2 - t / 2
  }) : $(".full img").css({
    "margin-top": 0
  })
}

